import React from "react"
import img1 from './img_1.jpeg'
import img2 from './img_2.jpeg'
import { Button } from 'react-bootstrap'
export default class Animation extends React.Component{
    constructor(props) {
        super(props);
            this.images = [
                img1, img2, 
            ];
        this.next = this.next.bind(this);
        this.state = { 
            index:0,
            cnt:0
        }

    }

    componentDidMount(){
        this.next();
    }

    next(){
        setTimeout(() => {
            this.setState({
                index: (this.state.index+1)%2,
                cnt: this.state.cnt+1,
            }) ; // %n where n in the number of images
            this.next();
        },300);
    }

    render(){
        return(
            <p>
            <img 
                src={this.images[this.state.index]}
                alt="images"
                height="100%"
                width="100%"
            />
            </p>
        )
    }

}
