import React from 'react'
import {Button,ButtonToolbar,ProgressBar} from 'react-bootstrap'
import images from './../image.js'
import audios from './../audios.js'
const correctI = [ true , false , false , true ,true ,true,false,false,true,false]
const correctA= [ true,false,true,false,true,false,false,false,true,false]
const len = 9 // number of image in the set -1 
export default class Question extends React.Component{
    constructor(props){
        super(props);
        this.state = { 
            index: 0,
            status: 0,
            resultI : 0,
            resultA : 0,
            disabled : false 
        }
    }

    checkAnswer = (value,type) => {
        if (type === 'i') {
            if (value === correctI[this.state.index]) {
                this.setState({ resultI : this.state.resultI+1})
            }
        }else{
            if (value === correctA[this.state.index]) {
                this.setState({ resultA : this.state.resultA+1})
            }
        }
    }   

    handleClickY = () => {
        let idx = this.state.index+1;
        if (this.state.status === 0) {
            this.checkAnswer(true,'i')
            if (idx  > len) {
                this.setState({
                    status : this.state.status+1,
                    index : 0,
                    disabled : true
                }) 
            }else{
                this.setState({ index : idx})
            }
        }else{
            this.checkAnswer(true,'a')
           if ( idx > len) {
               this.props.handleResult(this.state.resultI,this.state.resultA)
               this.props.handleNext()
           }else{
               this.setState({ 
                   index : idx ,
                   disabled : true 
               })
           }
        }
        console.log(this.state.resultA)
        console.log(this.state.resultI)
    }

    handleClickN = () => {
        let idx = this.state.index+1;
        if (this.state.status === 0) {
            this.checkAnswer(false,'i')
            if ( idx > len) {
                this.setState({ 
                    status : this.state.status+1,
                    index : 0 ,
                    disabled : true,
                }) 
            }else{ 
                this.setState({index: idx})
            }
        }else{
            this.checkAnswer(false,'a')
           if ( idx > len) {
               this.props.handleResult(this.state.resultI,this.state.resultA)
               this.props.handleNext()
           }else{
               this.setState({ 
                   index : idx ,
                   disabled : true 
               })
           }
        }
        console.log(this.state.resultA)
        console.log(this.state.resultI)
    }

    handlePlay = () => {
        let audio = document.getElementById("audio")
        audio.play()
        this.setState({ disabled : false })
    }

    render(){
        const state = this.state.status
        const index = this.state.index 
        let question 
        if (state === 0) {
            question =  <div>
                            <div style={{height : this.props.height/3}}>
                            </div>
                            <img
                                style={{  width : 500}}
                                src={images[index]}
                                alt="images"
                            />
                        </div>
        }else{
            question =  <div>
                            <div style={{height : this.props.height/2}}>
                            </div>
                            <audio 
                                id="audio"
                                src={audios[index]}
                            >
                                The Browser do not support the audio tag 
                            </audio>
                            <Button 
                                onClick={this.handlePlay} 
                                bsStyle='primary'
                                bsSize='large'
                            > PLAY
                            </Button>
                            <div style={{height : this.props.height/6}}>
                            </div>
                        </div>
        }
        return (
            <div style={{height: this.props.height , backgroundColor: '#212F3D'}}>
                {question}
                <ButtonToolbar style={{marginLeft:(this.props.width/2)-250}}>
                    <Button 
                        style={{marginRight:375}} 
                        onClick={this.handleClickY} 
                        bsStyle='primary'
                        bsSize='large'
                        disabled={this.state.disabled}
                    >
                        Yes
                    </Button>{' '}
                    <Button 
                        onClick={this.handleClickN} 
                        bsStyle='primary'
                        bsSize='large'
                        disabled={this.state.disabled}
                    >
                        No
                    </Button>
                </ButtonToolbar>
            <ProgressBar striped now={this.state.index*10} style={{bottom:0 , position: 'fixed' , width: this.props.width}} />
            </div>
        )
    }
}
