import React from 'react'
import {Panel,Button} from 'react-bootstrap'

export default class Home extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            status: 0,
        }
    }

    handleConsensus = () => {
        this.setState({ status : 1})
    }


    render(){
        const state = this.state.status
        let body 

        if (state===0) {
            body=(
                <div>    
                <Panel>
                    <Panel.Heading>
                        <Panel.Title componentClass="h2"> Consensus</Panel.Title>
                    </Panel.Heading>
                    <Panel.Body>
                        the test for the consensus
                    </Panel.Body>
                </Panel>
                <Button onClick={this.handleConsensus} bsStyle='primary' bsSize='large'>Accept and Continue</Button>
               </div> 
            )
        }else{
            body=(
                <div>
                <Panel>
                    <Panel.Heading>
                        <Panel.Title componentClass="h2"> Instruction</Panel.Title>
                    </Panel.Heading>
                    <Panel.Body>
                        the instruction for the tester
                    </Panel.Body>
                </Panel>
                <Button onClick={this.props.handleClick} bsStyle='primary' bsSize='large'> Press to Start</Button>
                </div>
            )
        }

        return (
        <Panel style={{height:this.props.height}} >
            <Panel.Heading>
            <Panel.Title componentClass='h1'>Memory app 0.2</Panel.Title >
            </Panel.Heading>
            <Panel.Body>
                {body}
            </Panel.Body>
        </Panel> 
        );   
    }
}
