import React from 'react'
import { Form, FormGroup,FormControl,ControlLabel,Button} from 'react-bootstrap'
const def = "male"
export default class FormComponent extends React.Component{
    constructor(props){
        super(props);
        this.state= {
            age: undefined,
            gender: def,
            other : undefined,
        }
    }


    handleAge= (e) => {
        this.setState({ age: e.target.value});
    }

    handleGender = (e) => {
        this.setState({ gender: e.target.value});
    }

    handleOther = (e) => {
        this.setState({ other : e.target.value});
    }

    render() {
        return(
            <div>
                <Form inline>
                    <FormGroup controlId='formInLineAge'>
                        <ControlLabel>Age</ControlLabel>{'  '}
                        <FormControl onChange={this.handleAge} type='number' placeholder='23'/>
                    </FormGroup>{'  '}
                    <FormGroup controlId='formInLineGender'>
                        <ControlLabel>Gender</ControlLabel>{'   '}
                        <FormControl componentClass='select' onChange={this.handleGender} placeholder={def} >
                            <option value="male">male</option>
                            <option value="female">female</option>
                            <option value="other">other</option>
                        </FormControl>
                    </FormGroup>
                </Form>
                <form>
                    <FormGroup controlId='formOther'>
                        <ControlLabel> Example for other stuff</ControlLabel>
                        <FormControl 
                                type='text'
                                onChange={this.handleOther}
                        />
                    </FormGroup>
                </form>
                <Button onClick={this.props.sendInfo} bsStyle='primary' bsSize='large'> Submit </Button>
            </div>
        )
    }

}
