import React from 'react'
import {Button,ButtonToolbar} from 'react-bootstrap'
import images from './../image.js'
import audios from './../audios.js'
const len = 9 // number of image in the set -1 
export default class Question extends React.Component{
    constructor(props){
        super(props);
        this.state = { 
            index: 0,
            status: 0,
            result : 0,
            disabled : false 
        }
    }

    handleClickY = () => {
        let idx = this.state.index+1;
        if (this.state.status === 0) {
            if (idx  > len) {
                this.setState({
                    status : this.state.status+1,
                    index : 0,
                    disabled : true
                }) 
            }else{
                this.setState({ index : idx})
            }
        }else{
           if ( idx > len) {
               this.props.handleNext()
           }else{
               this.setState({ 
                   index : idx ,
                   disabled : true 
               })
           }
        }
    }

    handleClickN = () => {
        let idx = this.state.index+1;
        if (this.state.status === 0) {
            if ( idx > len) {
                this.setState({ 
                    status : this.state.status+1,
                    index : 0 ,
                    disabled : true,
                }) 
            }else{ 
                this.setState({index: idx})
            }
        }else{
           if ( idx > len) {
               this.props.handleNext()
           }else{
               this.setState({ 
                   index : idx ,
                   disabled : true 
               })
           }
        }
    }

    handlePlay = () => {
        let audio = document.getElementById("audio")
        audio.play()
        this.setState({ disabled : !this.state.disable })
    }

    render(){
        const state = this.stata.status
        let question 
        if (state === 0) {
            question =  <img
                            style={{  width : 500}}
                            src={images[this.state.index]}
                            alt="images"
                        />
        }else{
            question =  <div>
                            <audio 
                                src={audios[this.state.idex]}
                                id="audio"
                            >
                                The Browser do not support the audio tag 
                            </audio>
                            <Button 
                                onClick={this.handlePlay} 
                                bsStyle='primary'
                                bsSize='large'
                            > PLAY
                            </Button>
                        </div>
        }
        return (
            <div style={{height: this.props.height , backgroundColor: '#212F3D'}}>
                <div style={{height : this.props.height/3}}>
                </div>
                {question}
                <ButtonToolbar style={{marginLeft:(this.props.width/2)-50}}>
                    <Button 
                        style={{marginRight:40}} 
                        onClick={this.handleClickY} 
                        bsStyle='primary'
                        bsSize='large'
                        disabled={this.state.disabled}
                    >
                        Yes
                    </Button>{' '}
                    <Button 
                        onClick={this.handleClickN} 
                        bsStyle='primary'
                        bsSize='large'
                        disabled={this.state.disabled}
                    >
                        No
                    </Button>
                </ButtonToolbar>
            </div>
        )
    }
}
